import React, { useState, useReducer, useContext, createContext, useEffect } from 'react';
import userService from '../services/userService';
import userReducer from '../reducers/userReducer';
import { CreatureContext } from './CreatureContext';

export const UserContext = createContext();

const UserContextProvider = (props) => {
  const [user, dispatch] = useReducer(userReducer, {}, () => {
    const storedUser = localStorage.getItem('my-user');
    return storedUser ? JSON.parse(storedUser) : {};
  });

  const [userInfo, setUserInfo] = useState({
    username: '',
    email: '',
    password: '',
  });

  const [signup, setSignup] = useState(true);

  useEffect(async () => {
    const users = await userService.getAll();

    const thisUser = users.filter(acct => acct.username === userInfo.username);

    // find user that matches login
    // return .readUser(userId) method
    console.log(thisUser);
  }, []);

  useEffect(() => {
    localStorage.setItem('my-user', JSON.stringify(user));
  }, [userInfo]);

  const toggleSignup = () => {
    return setSignup(!signup);
  }

  const addUser = async (userObject) => {
    const users = await userService.getAll();

    console.log(users);
  }

  const login = userObject => {
    const { username, email, password } = userObject;

    setUserInfo({
      ...userInfo,
      username,
      email,
      password
    });
  }

  const deleteUser = userObject => {}

  return (
    <UserContext.Provider value={{
      user,
      signup,
      toggleSignup,
      addUser,
      login,
      deleteUser,
    }}>
      {props.children}
    </UserContext.Provider>
  )
}

export default UserContextProvider;
