import axios from 'axios';
const baseUrl = '/dashboard/user';

// CREATE user
const createUser = async (userObject) => {
  const req = await axios.post(`${baseUrl}/create`, userObject);
  return req.then(res => res.data);
};

// READ user
const readUser = async (userId) => {
  const req = await axios.get(`${baseUrl}/${userId}`);
  return req.then(res => res.data);
};

// UPDATE user
const updateUser = async (userId, userObject) => {
  const req = await axios.put(`${baseUrl}/${userId}`, userObject);
  return req.then(res => res.data);
};

// DELETE user
const deleteUser = async (userId, userObject) => {
  const req = await axios.delete(`${baseUrl}/${userId}`, userObject);
  return req.then(res => res.data);
};

export default { createUser, readUser, updateUser, deleteUser };
