const userReducer = (state, action) => {
  switch(action.type) {
    case 'LOG-IN':
      return {
        ...state,
        username: action.user.username,
        email: action.user.email,
        password: action.user.password,
      }
    default:
      return state;
  }
}

export default userReducer;
