import React, { useState, useContext } from 'react';
import { Link, Route, Redirect } from 'react-router-dom';

import { CreatureContext } from '../../contexts/CreatureContext';
import { UserContext } from '../../contexts/UserContext';

import * as stats from '../../modules/stats';
import * as age from '../../modules/age';
import { getMegas, putMegas } from '../../modules/create';
import { otherVersions } from '../../modules/pokemonList';

import CreatureOption from './CreatureOption';

import { v4 as uuidv4 } from 'uuid';
import pokemon from 'pokemon';


const CreatureForm = () => {
  const { creatures, formDisplay, toggleFormDisplay, dispatch, createCreature, finish } = useContext(CreatureContext);
  const { user, userDispatch } = useContext(UserContext);

  const pkmnArr = pokemon.all().sort((a, b) => {
    return a > b ? 1 : 
      a < b ? -1 : 0;
  });

  const OptionComponents = pkmnArr.map(creature => {
    return (
      <CreatureOption 
        key={pokemon.getId(creature)}
        creature={creature}
      />
    );
  });

  const [creature, setCreature] = useState({
    firstCreature: '',
    firstMega: '',
    secondCreature: '',
    secondMega: '',
    thirdCreature: '',
    thirdMega: '',
    fourthCreature: '',
