import React, { useState, useReducer, createContext, useEffect } from 'react';
import userService from '../services/userService';
import userReducer from '../reducers/userReducer';

export const UserContext = createContext();

const UserContextProvider = (props) => {
  const [user, dispatch] = useReducer(userReducer, {}, () => {
    const storedUser = localStorage.getItem('my-user');
    return storedUser ? JSON.parse(storedUser) : {};
  });

  const [signup, setSignup] = useState(true);
  const [error, setError] = useState({
    error: null
  });

  useEffect(() => {
    localStorage.removeItem('my-user');
    localStorage.setItem('my-user', JSON.stringify(user));

    console.log(user);
  }, [user]);

  const toggleSignup = () => {
    return setSignup(!signup);
  }

  const addUser = async (userObject) => {

    userService.createUser(userObject)
      .then(res => {
        if (res === false) {
          setError({
            ...error,
            error: true
          });
        } else {
          setError({
            ...error,
            error: null
          });
        }
      })
      .catch(err => console.error(err));
  }

  const getLocalCreatures = () => {
    const creatureStorage = localStorage.getItem('my-creatures');

    return creatureStorage ? JSON.parse(creatureStorage) : null;
  }

  const getUserInfo = async (accessToken, userObject) => {
    const storedCreatures = await getLocalCreatures();

    const user = await userService.readUser(userObject._id);

    const { username, email, password } = user;

    dispatch({ type: 'LOG_IN', user: {
      accessToken, username, email, password, storedCreatures,
    }});
  }

  const login = async (userObject) => {
    const { username, password } = userObject;

    const accessToken = await userService.login({
      username,
      password
    })
      .then(res => {
        console.log(res.accessToken);
        return res.accessToken;
      })
      .catch(err => {
        console.error(err);
      });

    console.log(accessToken !== null);

    if (accessToken !== null) {
      return getUserInfo(accessToken, userObject); 
    }
  }

  const deleteUser = userObject => {}

  return (
    <UserContext.Provider value={{
      user,
      signup,
      toggleSignup,
      error,
      setError,
      addUser,
      login,
      deleteUser,
    }}>
      {props.children}
    </UserContext.Provider>
  )
}

export default UserContextProvider;
