import React, { useState } from 'react';
import { Route, Redirect } from 'react-router-dom';
import userService from '../../services/userService';

const ForgotPassword = () => {
  /*
  enter email and receive reset code via email
  if correct, redirect to reset code form
  */

  const [form, setForm] = useState({ email: '' });

  const [error, setError] = useState({ upset: false });

  const [code, setCode] = useState(false);

  const handleChange = event => {
    const { name, value } = event.target;

    setForm({
      ...form,
      [name]: value
    });
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

    setError({
      ...error,
      upset: false
    });

    let emailExists;

   if (form.email.trim()) {
      emailExists = await userService.postResetRequest(form.email);
    } else {
      return setError({
        ...error,
        upset: true
      });
    }

    if (emailExists) {
      const codeSent = await userService.getResetCode(form.email);
      setCode(!code);
    }

    setForm({
      ...form,
      username: '',
      email: '',
    });
  }

  if (code) {
    return (
      <Route exact path="/user/reset/forgot">
        <Redirect to="/user/reset/code" />
      </Route>
    )
  }

  return (
    <div className="forgot-password-form">
      <form onSubmit={handleSubmit}>
        <div className="email-input">
          <label>Enter Email: </label>
          <input 
            type="text"
            name="email"
            value={form.email}
            onChange={handleChange}
          />
        </div>

        <div className="get-reset-code">
          <button>Get Code</button>
        </div>
      </form>
    </div>
  )
}

export default ForgotPassword;
