import React, { useState, useEffect, useContext } from 'react';
import { Link, Route, Redirect } from 'react-router-dom';
import LoginForm from './user/LoginForm';
import { UserContext } from '../contexts/UserContext';
import { CreatureContext } from '../contexts/CreatureContext';

const Welcome = () => {
  const { user, error, setError, userLoading } = useContext(UserContext);
  const { loading } = useContext(CreatureContext);

  const [welcomeLoad, setWelcomeLoad] = useState(true);

  useEffect(() => {
    setError({
      ...error,
      error: null
    });

    const timer = setTimeout(() => {
      setWelcomeLoad(!welcomeLoad);
    }, 700);

    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="index-container">
      {
        userLoading ?
          <h2>Loading user...</h2> :

        welcomeLoad ?
          <Route exact path="/">
            <Redirect to="/creatures" />
          </Route> :

          error === true ?
            <Route exact path="/">
              <Redirect to="/user/create" />
            </Route> :

            <div className="welcome">
              <LoginForm />

              <h1>Don't have an account?</h1>
              <Link to="/user/create">
                <p>Create an Account</p>
              </Link>
            </div>
      }
    </div>
  )
}

export default Welcome;
