import React, { useState, useContext } from 'react';
import { Link, Route, Redirect } from 'react-router-dom';
import { UserContext } from '../../contexts/UserContext';
import userService from '../../services/userService';

const ResetPassword = () => {
  const { reset } = useContext(UserContext);

  const [form, setForm] = useState({
    password: '',
    confirmPassword: ''
  });

  const [error, setError] = useState({ upset: false });

  const [showPassword, setShowPassword] = useState(false);

  const handleChange = event => {
    const { name, value } = event.target;

    setForm({
      ...form,
      [name]: value
    });
  }

  const handleSubmit = event => {
    event.preventDefault();

    setError({
      ...error,
      upset: false
    });

    if (form.password.trim() && form.confirmPassword.trim()) {
      if (form.password === form.confirmPassword) {
        userService.putPasswordReset(form.password, reset.resetToken);
      } else {
        return setError({
          ...error,
          upset: true
        });
      }
    } else {
      return setError({
        ...error,
        upset: true
      });
    }
  }

  const viewPassword = event => {
    event.preventDefault();

    return setShowPassword(!showPassword);
  }

  if (reset.passwordReset) {
    return (
      <Route exact path="/user/reset/password">
        <Redirect to="/" />
      </Route>
    )
  }

  return (
    <div className="reset-password-container">
      <Link to="/">Return Home</Link>

      <form onSubmit={handleSubmit}>
        <div className="password-input">
          <label>New Password: </label>
          <input 
            type={showPassword ? 'text' : 'password'}
            name="password"
            value={form.password}
            onChange={handleChange}
          />
          <button onClick={viewPassword}>&#128065;</button>
        </div>

        <div className="confirm-password-input">
          <label>Confirm New Password: </label>
          <input 
            type={showPassword ? 'text' : 'password'}
            name="confirmPassword"
            value={form.confirmPassword}
            onChange={handleChange}
          />
        </div>

        <div className="submit-password-reset">
          <button onClick={handleSubmit}>Reset Password</button>
        </div>
      </form>
    </div>
  )
}

export default ResetPassword;
