import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { UserContext } from '../../contexts/UserContext';
import userService from '../../services/userService';

const LoginForm = () => {
  const [user, setUser] = useState({
    username: '',
    email: '',
    password: ''
  });

  const [error, setError] = useState({ ref: '' });

  const handleChange = event => {
    const { name, value } = event.target;

    setUser({
      ...user,
      [name]: value
    });
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

    setError({
      ...error,
      ref: ''
    });

    let loginResult;

    if (user.username.trim()) {
      loginResult = await userService.login('username', user);
      console.log('loginResult', loginResult);
      return;
    } else if (user.email.trim()) {
      loginResult = await userService.login('email', user);
      console.log('loginResult', loginResult);
      return;
    } else if (!user.username.trim()) {
      return setError({
        ...error,
        ref: 'username'
      });
    } else if (!user.email.trim()) {
      return setError({
        ...error,
        ref: 'email'
      });
    }

    setUser({
      ...user,
      username: '',
      email: '',
      password: ''
    });
  }

  return (
    <div className="login-form-container">
      <form onSubmit={handleSubmit}>
        <div className="username-login">
          <label>Username: </label>
          <input 
            type="text"
            name="username"
            value={user.username}
            required={!user.email.trim()}
            onChange={handleChange}
          />
        </div>

        <div className="email-login">
          <label>or Email: </label>
          <input 
            type="text"
            name="email"
            value={user.email}
            required={!user.username.trim()}
            onChange={handleChange}
          />
        </div>

        <div className="password-login">
          <label>Password: </label>
          <input 
            type="password"
            name="password"
            value={user.password}
            required={true}
            onChange={handleChange}
          />
        </div>

        <Link to="/user/reset/forgot">Forgot Password?</Link>

        <button>Login</button>
      </form>
    </div>
  )
}

export default LoginForm;
