import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CreatureContext } from '../contexts/CreatureContext';

const CreateButton = () => {
  const { creatures } = useContext(CreatureContext);

  if (creatures.length === 6) {
    return (
      <div className="team-full">
        <span className="full-text">Your team of Creatures is full :)</span>
      </div>
    )
  }

  return (
    <div className="create-btn-container">
      <Link to="/create"><button className="create-btn"><p>New Creature</p></button></Link>
    </div>
  )
}

export default CreateButton;
